import { useState } from "react"
const Form = ({createTodo}) => {
    const [addTodo, setAddTodo] = useState("")
    const add = (e) => {

        const newTodo = {
            id: Math.floor(Math.random() * 1e5),
            content: addTodo
        }

        createTodo(newTodo)
    }
    return (
        <div>
            <input type="text"
                onChange={(e) => setAddTodo(e.target.value)}
                value={addTodo}
            />
            <button onClick={add}>add</button>
        </div>
    )
}

export default Form
