import { useState } from "react";

const Child = ({ state, setState }) => {

  const add = () => {
    setState({value: 2});
  }
  return (
    <>
      <span>{state.value}</span>
      <button onClick={add}>+</button>
    </>
  );
};

const Example = () => {
  const [ state, setState ] = useState({ value: 0 });

  return (
    <>
      <div>
        <Child state={state} setState={ setState} />
      </div>
    </>
  );
};

export default Example;
