const Example = () => {

  //関数型
  const nums = [1,2,3]
  const sum = (arr) => {
    const nums = arr;
    let sumValue = 0;
    for(let i = 0; i < nums.length; i++) {
      sumValue += nums[i];
    }
    return sumValue;
  }

  // オブジェクト型
  const numObj = {
    nums: [1,2,3],
    sum() {
      const nums = this.nums;
      let sumValue = 0;
      for(let i = 0; i < nums.length; i++) {
        sumValue += nums[i];
      }
      return sumValue;
    }
  }
  return (
    <>
      <h3>状態管理と処理を分離:{numObj.sum() }</h3>
      <p>状態（データ）と処理（やりたいこと）は切り離す</p>
    </>
  );
};

export default Example;
