import {useState, useReducer} from 'react'

const Example = () => {

  const [state, setState] = useState(0);
  const [rstate, dispatch] = useReducer((prev, action) => {
    if (action === '+') {
      ++prev
    } else {
      --prev
    }

  }, 0)

  const add = () => {
    setState(prev => ++prev)
  }
  const addReducer = () => {
    dispatch('+')
  }
  const downReducer = () => {
    dispatch('-')
  }
  return (
    <>
      <div>
        <h3>useState</h3>
        <span>{state} : </span>
        <button onClick={add}>+</button>
      </div>
      <div>
        <h3>useReducer</h3>
        <span>{rstate} : </span>
        <button onClick={addReducer}>+</button>
        <button onClick={downReducer}>-</button>
      </div>
    </>
  );
};

export default Example;
